mapper.update:
update tbl_board
set title = #{title}, content = #{content}
where bno = #{bno}

update tbl_board
set viewcnt = viewcnt + #{cnt}
where bno = #{bno}

update tbl_board
set replycnt = replycnt + #{cnt}
where bno = #{bno}

update tbl_board
set likecnt = likecnt + #{cnt}
where bno = #{bno}

mapper.select:
select count(fullName)
from tbl_attach
where bno = #{bno}
